mapper.update:
UPDATE IGNORE MEMBER
SET
MB_NAME=#{mb_name}, MB_PHONE = #{mb_phone}, MB_EMAIL= #{mb_email}

,MB_PW = #{mb_pw}

WHERE
MB_ID = #{mb_id}

mapper.select:
SELECT * FROM MEMBER WHERE 1=1
AND MB_ID = #{mb_id}
AND MB_EMAIL = #{mb_email}
AND MB_YN = 'Y'

