mapper.update:
UPDATE EXHIBITION SET EXH_APPROVAL=1
WHERE
EXH_NO=#{exh_no}

mapper.delete:
DELETE FROM exhibition
WHERE 1=1
AND EXH_NO=#{exh_no}

mapper.select:
SELECT * FROM (SELECT e.* ,COUNT(g.MB_ID)AS RANK_CNT FROM EXHIBITION AS e LEFT JOIN GOOD AS g ON e.EXH_NO=g.EXH_NO WHERE 1=1 AND DATE(EXH_END_DATE) >= DATE(NOW()) AND EXH_APPROVAL=1 GROUP BY EXH_NO) AS rk WHERE 1=1
AND REPLACE(UPPER(EXH_TITLE),' ','') LIKE REPLACE(CONCAT('%',(UPPER(#{eSearchKeyword})),'%'),' ','') OR REPLACE (UPPER(EXH_HALL),' ','') LIKE REPLACE(CONCAT('%',(UPPER(#{eSearchKeyword})),'%'),' ','')
AND exh_local_name= #{exh_local_name}
ORDER BY EXH_ST_DATE DESC LIMIT 16 OFFSET #{offset}
