mapper.select:

SELECT COUNT(*) FROM FAQ WHERE 1=1 

ORDER BY FAQ_NO DESC
LIMIT 10 OFFSET #{offset}

select.if:
SELECT * FROM FAQ WHERE 1=1 
 ORDER BY FAQ_NO DESC
LIMIT 10 OFFSET #{offset}

SELECT * FROM FAQ WHERE 1=1 
 ORDER BY FAQ_NO DESC
LIMIT 10 OFFSET #{offset}

mapper.update:
UPDATE FAQ 
SET 
FAQ_TITLE=#{faq_title},
FAQ_CONTENT=#{faq_content}
WHERE FAQ_NO=#{faq_no}
