mapper.select:
SELECT COUNT(*) FROM ONEONE WHERE 1=1
AND REPLACE(UPPER(ONE_TITLE),' ','') LIKE REPLACE(CONCAT('%',(UPPER(#{searchKeyword})),'%'),' ','') OR REPLACE (UPPER(ONE_WRITER),' ','') LIKE REPLACE(CONCAT('%',(UPPER(#{searchKeyword})),'%'),' ','')
ORDER BY ONE_NO DESC LIMIT 10 OFFSET #{offset}

SELECT * FROM ONEONE WHERE 1=1
AND REPLACE(UPPER(ONE_TITLE),' ','') LIKE REPLACE(CONCAT('%',(UPPER(#{searchKeyword})),'%'),' ','') OR REPLACE (UPPER(ONE_WRITER),' ','') LIKE REPLACE(CONCAT('%',(UPPER(#{searchKeyword})),'%'),' ','')
ORDER BY ONE_NO DESC LIMIT 10 OFFSET #{offset}

mapper.update:
UPDATE ONEONE
SET ONE_ANSWER=#{one_answer} 
WHERE ONE_NO=#{one_no}
